export default {
  name: "RedRain",
  data: function data() {
    return {
      clickNum: 0,
      animationtime: null,
      countDownTime: null
    };
  },
  methods: {
    addBao: function addBao(left, height, src) {
      var div = document.createElement("div");
      var img = document.createElement("img");
      div.appendChild(img);
      img.className = "roll";
      img.src = src;
      div.style.left = left + "px";
      div.style.height = height + "px";
      div.classList.add("redBox");
      document.getElementById("redzone").appendChild(div);
      var _this = this;
      div.addEventListener('click', function (event) {
        _this.ckickRed(event);
      });
      setTimeout(function () {
        document.getElementById("redzone").removeChild(div);
      }, 4000);
    },
    ckickRed: function ckickRed(event) {
      console.log(event);
      this.clickNum++;
      console.log('点击到红包了:' + this.clickNum);
    },
    start: function start() {
      var _this = this;
      if (this.animationtime) {
        clearTimeout(this.animationtime);
        this.animationtime = null;
      }
      // 开始倒计时
      this.countDown(10);
      this.animationtime = setInterval(function () {
        var left = Math.random() * (document.body.scrollWidth - 100);
        var height = Math.random() * document.body.scrollHeight;
        var src = require('@/assets/img/gift.png');
        _this.addBao(left, height, src);
      }, 500);
    },
    // 倒计时
    countDown: function countDown(time) {
      this.countDownTime && clearTimeout(this.countDownTime);
      var num = 0;
      var _this = this;
      this.countDownTime = setInterval(function () {
        num++;
        console.log(num);
        if (num >= time) {
          clearTimeout(_this.countDownTime);
          _this.end();
        }
      }, 1000);
    },
    end: function end() {
      this.animationtime && clearTimeout(this.animationtime);
      this.animationtime = null;
    }
  },
  mounted: function mounted() {}
};
